mapper.select:

select * 
from(
	select rownum as rnum,SearchBoardResult.*
	from (
		select *
		from board
		where bno is not null

		order by regDate desc
	)	SearchBoardResult

	where rownum <= #{endRow}

)

where rnum >= #{startRow}

mapper.update:
update board
set viewcnt=viewcnt+1
where bno =#{bno}

update board
set
title=#{title},content=#{content}
where bno=#{bno}
